package oops;

public class Whileprg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// while loop
		int i=1;
		while(i<=10){
			System.out.println(i);
			i=i+1;
		}
		//for loop ,j++=J=1;
		// loop
		for(int j=1;j==10;j++){
			System.out.println(j);
			
		}
		
		
		

	}

}
